var SCM = SCM || {};

// SCM.nav = SCM.nav || {};

SCM.nav = (function(SCM, $){
    var $body = $('body'),
        $navBtn = $('#header__btn__acct'),
        $navDrop = $navBtn.parents('.header__account'),
        $footer = $('.turfbuilder').find('.footer-container'),
        $scrollBtn = $('#btn__top'),
        $scrollBtnLink = $scrollBtn.find('.scroll-link'),
        $overlay = $('.page__overlay');



    function init() {
        if ($('.turfbuilder__header').length) {
            attachScrollBtn(true);
            bindEvents();
        }
    }


    function resetMobileNav() {
        // console.log('resetting');
        $body.removeClass('nav-active scroll-lock');
    }


    function toggleHeaderNav() {
        if ($body.hasClass('nav-active scroll-lock')) {
            resetMobileNav();
        } else {
            $body.addClass('nav-active scroll-lock');
        }
    }


    function attachScrollBtn(bool) {
        if (bool === true) {
            $scrollBtn.addClass('attach');
            $footer.addClass('btn-sticky');
        } else {
            $scrollBtn.removeClass('attach');
            $footer.removeClass('btn-sticky');
        }
    }


    function bindEvents() {
        // toggle mobile nav
        $navBtn.on('click', toggleHeaderNav);
        $overlay.on('click', toggleHeaderNav);


        // hide mobile nav on desktop sizes
        $(window).on('resize', SCM.util.debounce(function() {
            if ($(window).width() > 771 && $('body').hasClass('scroll-lock')) {
                resetMobileNav();
            }
        }, 200));


        // show/hide cta scroll button
        if ($scrollBtn.length) {
            $('.turfbuilder .section__steps').waypoint(function(direction){
                var topDir = (direction == 'up' ? true : false );
                attachScrollBtn(topDir);
            }, {
                offset: 'bottom-in-view'
            });

            $('.turfbuilder.cms-index-html .footer').waypoint(function(direction){
                var btmDir = (direction == 'up' ? false : true );
                attachScrollBtn(btmDir);
            }, {
                offset: '100%'
            });
        }


        // scroll to cta section
        $scrollBtnLink.on('click', function(e) {
            var $linkTarget = $(this).attr('href');

            e.preventDefault();

            $('html, body').animate({
                scrollTop: $($linkTarget).offset().top
            }, 500);
        });
    }




    return {
        init: init
    };

})(SCM, jQuery);

SCM.slider = (function(SCM, $){
    var $slideContainer = '.section__tabs--tabtrack',
        $slides = '.tabpanel',
        $slideLabel = $('.tabnav-label');




    function init() {
        setTimeout(function() {
            setSlideHeight($slides, $slideContainer);
        }, 200);

        bindEvents();
    }


    function setSlideHeight(slide, wrap) {
        var maxHeight = 0;

        $(slide).each(function() {
            if ($(this).height() > maxHeight) {
                maxHeight = $(this).height();
            }
        });

        $(wrap).css('height', maxHeight);
    }



    function bindEvents() {
        $(window).on('resize', SCM.util.debounce(function() {
            // console.info('resizing slide');
            setSlideHeight($slides, $slideContainer);
        }, 250));


        // tabbed content sections
        $slideLabel.on('click', function() {
            var $el = $(this);

            $el.siblings('label').removeClass('active');
            $el.addClass('active');
        });
    }



    return {
        init: init
    };

})(SCM, jQuery);

SCM.util = (function(SCM, $){


    function init() {
        PointerEventsPolyfill.initialize();
    }


    // Returns a function, that, as long as it continues to be invoked, will not
    // be triggered. The function will be called after it stops being called for
    // N milliseconds. If `immediate` is passed, trigger the function on the
    // leading edge, instead of the trailing.
    function debounce(func, wait, immediate) {
        var timeout;
        return function() {
            var context = this, args = arguments;
            var later = function() {
                timeout = null;
                if (!immediate) func.apply(context, args);
            };
            var callNow = immediate && !timeout;
            clearTimeout(timeout);
            timeout = setTimeout(later, wait);
            if (callNow) func.apply(context, args);
        };
    }






    return {
        init: init,
        debounce: debounce
    };

})(SCM, jQuery);

SCM.router = (function(SCM, $){

    function init() {
        ///////////////////////
        // Auto start        //
        ///////////////////////

        // util
        SCM.util.init();


        // navigation
        SCM.nav.init();


        ///////////////////////
        // Conditional Start //
        ///////////////////////

        if ($('.section__tabs').length) {
            SCM.slider.init();
        }


    }

    return {
        init: init
    };

})(SCM, jQuery);

SCM.router.init();
